const initialState = [
    { id: 0, name: "Raman Sharma", Level:  " 20", Progress: "Master" },
    { id: 1, name: "Test Name", Level:  " 25", Progress: "Master" },
  ];
  
  export const contactReducer = (state =  initialState , action) => {
    switch (action.type) {
      case "ADD_CONTACT":
        return {
          ...state,
          contactReducer: [...state.contactReducer, action.payload]
        };
      case "DELETE_CONTACT":
        return {
          ...state,
          contactReducer: state.contactReducer.filter((contact) =>
            contact.id === action.payload ? null : contact
          )
        };
      case "UPDATE_CONTACT":
        return {
          ...state,
          contactReducer: state.contactReducer.filter((contact) =>
            contact.id === action.payload.id
              ? Object.assign(contact, action.payload)
              : contact
          )
        };
      case "RESET_CONTACT":
        return {
          ...state,
          contactReducer: [{ name: null, Level: null, Progress: null }]
        };
      default:
        return state;
    }
  };
